mapper.insert:
INSERT INTO TB_WISH_PRODUCT
(
  WISHLIST_SEQ
, PROD_CD
, PROD_NM
, PROD_IMG
, REG_DT
, UP_DT
)
VALUES
(
  #{wishlistSequence}
, #{productCode}
, #{productName}
, #{productImgPath}
, NOW()
, NOW()
)

mapper.select:
SELECT COUNT(*)
FROM TB_WISHLIST
WHERE EMAIL = #{memberEmail}

SELECT COUNT(*)
FROM TB_WISHLIST TW
   , TB_WISH_PRODUCT TWP
WHERE TW.WISHLIST_SEQ = TWP.WISHLIST_SEQ
AND TW.EMAIL = #{memberEmail}
AND TWP.PROD_CD = #{productCode}

SELECT COUNT(*)
FROM TB_WISHLIST TW
   , TB_WISH_PRODUCT TWP
WHERE TW.WISHLIST_SEQ = TWP.WISHLIST_SEQ
  AND TW.EMAIL = #{memberEmail}
  AND TWP.WISHLIST_SEQ = #{wishlistSequence}

SELECT COUNT(1)
FROM TB_WISHLIST
WHERE EMAIL = #{memberEmail}
AND NAME = #{wishlistName}

SELECT COUNT(1)
FROM TB_WISHLIST
WHERE EMAIL = #{memberEmail}
